mapper.select:
SELECT t1.id, t1.block_name, t1.description, t1.create_time, t1.status, t2.big_type_name, t3.small_type_name, t4.username FROM block_application_block AS t1, block_big_type AS t2, block_small_type AS t3, sys_user AS t4 WHERE t1.big_type_id = t2.id AND t1.small_type_id = t3.id AND t1.user_id = t4.id
AND t2.id = #{blockApplicationBlockDto.bigTypeId}
AND t3.id = #{blockApplicationBlockDto.smallTypeId}
ORDER BY t1.update_time DESC,t1.status
