mapper.select:
SELECT t1.id, t1.reason, t1.status, t1.update_time, t5.username, t3.big_type_name, t4.small_type_name, t2.block_name FROM block_application_master AS t1, block_info AS t2, block_big_type AS t3, block_small_type AS t4, sys_user AS t5 WHERE t1.block_id = t2.id AND t2.big_type_id = t3.id AND t2.small_type_id = t4.id AND t1.user_id = t5.id and t1.status = 0
AND t3.id = #{blockApplicationMasterDto.bigTypeId}
AND t4.id = #{blockApplicationMasterDto.smallTypeId}
ORDER BY t1.update_time DESC
